#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="D:\Download\blog\node_modules\esbuild\bin\node_modules;D:\Download\blog\node_modules\esbuild\node_modules;D:\Download\blog\node_modules;D:\Download\node_modules;D:\node_modules;D:\Download\blog\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/Download/blog/node_modules/esbuild/bin/node_modules:/mnt/d/Download/blog/node_modules/esbuild/node_modules:/mnt/d/Download/blog/node_modules:/mnt/d/Download/node_modules:/mnt/d/node_modules:/mnt/d/Download/blog/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../esbuild/bin/esbuild" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../esbuild/bin/esbuild" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../esbuild/bin/esbuild" $args
  } else {
    & "node$exe"  "$basedir/../esbuild/bin/esbuild" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
